import React , { useState } from 'react';

export function InputFind(props){

    const [pokemon, setPokemon] = useState('');

    var content =
        <form>
            <input
            placeholder="Busca tu pokemon"
            type="text"
            onChange={text =>{  
                setPokemon(text.target.value);
            }}
            />
            <button
            type="submit"
            onClick={ () => { 
                var res = handleOnClick(pokemon);
                 } }>
                Buscar
            </button>
        </form>

        return content;
}


function handleOnClick(newPokemon){
    fetch("https://pokeapi.co/api/v2/pokemon/" + newPokemon)
        .then(res =>{
            return res.json();

        }).then(data =>{
            render(<h1>{data}</h1>)
        });
}

export default InputFind;
