import React , { useState } from 'react';
import { getPokemones } from '../services';

export function InputFind(props){
    const [pokemon, setPokemon] = useState('');

    var content = 
    <form>
        <input
        placeholder="Busca tu pokemon"
        type="text"
        onChange={text =>{  
            setPokemon(text.target.value);
        }}
        />
        <button
        type="submit"
        onClick={ () => {  setPokemon(handleOnClick(pokemon)) } }>
            Buscar
        </button>
    </form>

    return content;
}

async function handleOnClick(newPokemon){
    const res = await getPokemones(newPokemon);
    console.log(newPokemon);
    return res;
}

export default InputFind;
