import React , { useState } from 'react';
import axios from 'axios'

export function InputFind(props){

    const [pokemon, setPokemon] = useState('');
    const [resultado, setResultado] = useState('');

    let handleOnClick = () => {
            fetch("https://pokeapi.co/api/v2/pokemon/" + pokemon)
                .then(res => {
                    return res.json();
                })
                .then(data => {
                   setResultado(data);
                   console.log(data);
                });
    }

    var content =
        <form>
            <input
            placeholder="Busca tu pokemon"
            type="text"
            onChange={text =>{  
                setPokemon(text.target.value);
            }}
            />
            <button
            type="submit"
            onClick={ handleOnClick }>
                Buscar
            </button>
        </form>
        if(resultado){
            return <h1>{resultado.name}</h1>;
        }

        return content;
    }

export default InputFind;
