import React , { useState } from 'react';
import { getPokemones } from '../services';

export function InputFind(props){
    const [pokemon, setPokemon] = useState('');

    var content = 
    <form>
        <input
        placeholder="Busca tu pokemon"
        type="text"
        onChange={text =>{  
            setPokemon(text.target.value);
        }}
        />
        <button
        type="submit"
        onClick={ () => { handleOnClick(pokemon) } }>
            {pokemon}
        </button>
    </form>

    return content;
}

async function handleOnClick(newPokemon){
    fetch("https://pokeapi.co/api/v2/pokemon/" + newPokemon)
        .then(res =>{
            return res.json();
        })
        .then(data => {
            console.log(data.results)
        })
}

export default InputFind;
