import React from 'react';
import axios from 'axios';
import ListarPokemon from './ListarPokemon.js'

class BuscarPokemon extends  React.Component {
	constructor( props ) {
		super( props );

        this.state = {
            pokemon: '',
            resultado: {}
		};
	}

	handleOnInputChange = (event) => {
		const pokemon = event.target.value;
		console.log(pokemon);
		this.setState({ pokemon });
	}

	fetchSearchResults = () => {
		const searchUrl = 'https://pokeapi.co/api/v2/pokemon/' + this.state.pokemon;

		axios.get(searchUrl)
			.then((res) => {
				this.setState({
					resultado: res.data,
				});
			});
	};

	render() {
		return (
			<div>
				<h2>Buscar Pokemon</h2>				
				<label>
					<input
						type="text"
						placeholder="Busca un pokemon"
						onChange={this.handleOnInputChange}
					/>
					<input
						type="submit"
						name="Buscar"
						onClick={this.fetchSearchResults}
					/>
				</label>
				if(this.state.resultado)
					<ListarPokemon resultado = { this.state.resultado} />
			</div>
		)
	}
}
export default BuscarPokemon;
