import React , { useState } from 'react';
import axios from 'axios'
import { getPokemones } from '../services';

export function InputFind(props){

    const [pokemon, setPokemon] = useState('');
    const [resultado, setResultado] = useState('');

    let handleOnClick = async () => {
        console.log(pokemon);
        let res = await getPokemones(pokemon);
    }

    var content =
        <form>
            <input
            placeholder="Busca tu pokemon"
            type="text"
            onChange={text =>{  
                setPokemon(text.target.value);
            }}
            />
            <button
            type="submit"
            onClick={ handleOnClick }>
                Buscar
            </button>
        </form>

        if(resultado){
            return content + <h1>{resultado.name}</h1>;
        }
        return content;

    }

export default InputFind;
