import React from 'react';
import axios from 'axios';

class BuscarPokemon extends  React.Component {
	constructor( props ) {
		super( props );

        this.state = {
            pokemon: '',
            resultado: {}
		};
	}

	handleOnInputChange = (event) => {
		const pokemon = event.target.value;
		console.log(pokemon);
		this.setState({ pokemon });
	}

	renderSearchResults = () => {
		const {results} = this.state.resultado;

		if (Object.keys(results).length && results.length) {
			return (
				<div>
					{results.map((result) => {
						return (
							<h5>{result.name}</h5>
						);
					})}
				</div>
			);
		}
	};

	fetchSearchResults = () => {
		const searchUrl = 'https://pokeapi.co/api/v2/pokemon/' + this.state.pokemon;

		axios.get(searchUrl)
			.then((res) => {
				console.log(res.data);
				this.setState({
					resultado: res.data,
				});
			})
			.then(() => {
				this.renderSearchResults();	
			});
	};

	render() {
		return (
			<div>
				<h2>Buscar Pokemon</h2>				
				<label>
					<input
						type="text"
						placeholder="Busca un pokemon"
						onChange={this.handleOnInputChange}
					/>
					<input
						type="submit"
						name="Buscar"
						onClick={this.fetchSearchResults}
					/>
				</label>
			</div>
		)
	}
}
export default BuscarPokemon;
