import React, {useState, useEffect} from 'react';
import ListarPokemon from './ListarPokemon.js'
import { getPokemones } from '../services.js';

export default function BuscarPokemon() {

	const [resultado, setResultado] = useState([]);
	const [query, setQuery] = useState('');
	const [pokemons, setPokemons] = useState([]);

	useEffect(() => {
		(async () => {
			const res = await getPokemones();
			setResultado(res);
		})()
	}, []
	);

	function searchPokemon(){
		var pokemonsName = resultado.map( (pokemon) => {
			return pokemon.name;
		})
		var pokemonsFinded= pokemonsName.filter((p) => p.startsWith(query))
		setPokemons(pokemonsFinded);
		console.log(pokemonsFinded);
	}

	return (
		<div>
			<h2>Buscar Pokemon</h2>				
			<label>
				<input
					type="text"
					placeholder="Busca un pokemon"
					onChange={ (text) => {
						setQuery(text.target.value)
					} }
				/>
				<button
					type="submit"
					name="Buscar"
					onClick={ () => {searchPokemon()} }
				/>
			</label>
				<ListarPokemon 
					pokemons = { pokemons } 
				/>
		</div>
	);
}
