import React from 'react';
import axios from 'axios';
import ListarPokemon from './ListarPokemon.js'

class BuscarPokemon extends  React.Component {
	constructor( props ) {
		super( props );

        this.state = {
            pokemon: '',
			resultado: {},
			encontrado: false
		};
	}

	handleOnInputChange = (event) => {
		const pokemon = event.target.value;
		this.setState({ pokemon });
	}

	fetchSearchResults = () => {
		const searchUrl = 'https://pokeapi.co/api/v2/pokemon/search?q=' + this.state.pokemon;

		axios.get(searchUrl)
			.then((res) => {
				this.setState({
					resultado: res.data,
					encontrado: true
				});
			})
			.catch(() => {
				this.setState({
					encontrado: false
				})

			})			
	};

	render() {
		return (
			<div>
				<h2>Buscar Pokemon</h2>				
				<label>
					<input
						type="text"
						placeholder="Busca un pokemon"
						onChange={this.handleOnInputChange}
					/>
					<input
						type="submit"
						name="Buscar"
						onClick={this.fetchSearchResults}
					/>
				</label>
					<ListarPokemon 
						resultado = { this.state.resultado } 
						encontrado = { this.state.encontrado }
					/>
			</div>
		)
	}
}
export default BuscarPokemon;
