import React from 'react';
import axios from 'axios';

class BuscarPokemon extends  React.Component {
	constructor( props ) {
		super( props );

        this.state = {
            pokemon: '',
            resultado: {},
            message: '',
		};
	}

	handleOnInputChange = (event) => {
		const pokemon = event.target.value;
		console.log(pokemon);
		this.setState({ pokemon } );
	}

	renderSearchResults = () => {
		const {results} = this.state;
		if (Object.keys(results).length && results.length) {
			return (
				<div>
					{results.map((result) => {
					return (
						<h5>{result}</h5>
					);
				})}
				</div>
			);
		}
	};

	fetchSearchResults = (pokemon) => {
		const searchUrl = 'https://pokeapi.co/api/v2/' + pokemon;

		axios.get(searchUrl, {
				cancelToken: this.cancel.token,
			})
			.then((res) => {
				const resultNotFoundMsg = !res.data.hits.length
					? 'There are no more search results. Please try a new search.'
					: '';
				this.setState({
					results: res.data.hits,
					message: resultNotFoundMsg,
					loading: false,
				});
			})
	};

	render() {
		return (
			<div>
				<h2>Buscar Pokemon</h2>				
				<label>
					<input
						type="text"
						placeholder="Busca un pokemon"
						onChange={this.handleOnInputChange}
					/>
					<input
						type="submit"
						name="Buscar"
						onClick={this.fetchSearchthis.renderSearchResults}
					/>
				</label>
			</div>
		)
	}
}
export default BuscarPokemon;
