import React , { useState } from 'react';
import { getPokemones } from '../services';

export function InputFind(props){
    const [pokemon, setPokemon] = useState('');

    var content = <form>
    <input
      placeholder="Busca tu pokemon"
      type="text"
      onChangeText={text =>setPokemon(text)}
    />
    <input
      title="Ingresar"
      type="submit"
      onClick={()=>{
      handleOnClick(pokemon)}}/>
    </form>
}

async function handleOnClick(newPokemon){
    await getPokemones(newPokemon);
}

export default InputFind;
